import React from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000');

function App() {
  React.useEffect(() => {
    socket.on('connect', () => {
      console.log('Connected to server');
    });
    return () => {
      socket.off('connect');
    };
  }, []);

  return (
    <div>
      <h1>TaskMaster</h1>
      <p>Welcome to TaskMaster! The app is running.</p>
    </div>
  );
}

export default App;
